package Java_1201;

public class Practice_5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 10;
		int j = 20;
		
		int sum = i + j;
		int e = i - j;
		int k = i*j;
		int y = i/j;
		double d = (double)(i)/(double)(j);
		
		
		System.out.println("덧셈: " + sum);
		System.out.println("뼬셈: " + e);
		System.out.println("곱셈: " + k);
		System.out.println("나눗셈:" + y);
		System.out.println("나눗셈:" + d);
		
		
		

	}

}
