package Java_1207;

public class Fouroclock {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i =10;
		int j =7;
		
		int sum_1=i^j;
		System.out.println(sum_1);
		
		int sum_2=i|j;
		System.out.println(sum_2);

		int sum_3=i&j;
		System.out.println(sum_3);

		int sum_4=i>>j;
		System.out.println(sum_4);
		
		


	}

}
